import { useState } from "react"

const Condtionalrendering=()=>{
    const [isLoggedin,setLoggedin]=useState(false)

    let message;

    if(isLoggedin){
        message= <p>welcome, user</p>
    }
    else{
        message= <p>please login</p>
    }
    return <div>{message}</div>
}

export default Condtionalrendering
